const labels = $input.all();
const config = $('Config').item.json;
const targetLabelName = config.labelName;

const matchingLabel = labels.find(item => 
  item.json.name.toLowerCase() === targetLabelName.toLowerCase()
);

if (!matchingLabel) {
  throw new Error(`Label "${targetLabelName}" not found.`);
}

return {
  json: {
    labelId: matchingLabel.json.id,
    labelName: matchingLabel.json.name,
    firecrawlApiKey: config.firecrawlApiKey,
    processedLabelId: config.processedLabelId
  }
};

const items = $input.all();
const config = $('Find Label ID').item.json;

const hasEmail = items.length > 0 && items[0].json && items[0].json.id;

if (!hasEmail) {
  return {
    json: {
      hasEmail: false,
      emailId: null,
      labelId: config.labelId,
      firecrawlApiKey: config.firecrawlApiKey,
      processedLabelId: config.processedLabelId
    }
  };
}

const email = items[0].json;
return {
  json: {
    hasEmail: true,
    ...email,
    labelId: config.labelId,
    firecrawlApiKey: config.firecrawlApiKey,
    processedLabelId: config.processedLabelId
  }
};

const email = $input.item.json;
const config = $('Check Email Exists').item.json;

// Safely extract from field with multiple fallbacks
let fromRaw = '';
if (email.from && typeof email.from === 'object' && email.from.text) {
  fromRaw = email.from.text;
} else if (email.from && typeof email.from === 'string') {
  fromRaw = email.from;
} else if (email.headers && email.headers.from) {
  fromRaw = email.headers.from;
} else if (email.payload && email.payload.headers) {
  const fromHeader = email.payload.headers.find(h => h.name.toLowerCase() === 'from');
  fromRaw = fromHeader ? fromHeader.value : 'Unknown Sender';
} else {
  fromRaw = 'Unknown Sender';
}

const subject = email.subject || email.headers?.subject || 'No Subject';

// Extract email address and domain
let senderEmail = '';
let senderDomain = '';
const emailMatch = fromRaw.match(/<([^>]+)>/) || fromRaw.match(/([\w.-]+@[\w.-]+)/);
if (emailMatch) {
  senderEmail = emailMatch[1];
  senderDomain = senderEmail.split('@')[1] || '';
}

// Build company URL
let companyUrl = '';
const personalDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com', 'icloud.com', 'mail.com', 'protonmail.com'];
if (senderDomain && !personalDomains.includes(senderDomain.toLowerCase())) {
  companyUrl = 'https://' + senderDomain;
}

// Get email body with multiple fallbacks
let emailBody = '';
if (email.text && email.text.length > 10) {
  emailBody = email.text;
} else if (email.textPlain) {
  emailBody = email.textPlain;
} else if (email.textAsHtml) {
  emailBody = email.textAsHtml.replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
} else if (email.html) {
  emailBody = email.html
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/\s+/g, ' ')
    .trim();
} else if (email.snippet) {
  emailBody = email.snippet;
}

// Extract URLs from email
const urlRegex = /https?:\/\/[^\s<>"']+/gi;
const allUrls = emailBody.match(urlRegex) || [];

// Filter URLs
const excludePatterns = [
  /unsubscribe/i, /mailto:/i, /linkedin\.com/i, /twitter\.com/i,
  /facebook\.com/i, /instagram\.com/i, /calendly\.com/i, /zoom\.us/i,
  /meet\.google/i, /teams\.microsoft/i, /\.(png|jpg|jpeg|gif|svg|ico)$/i
];

const briefUrls = allUrls.filter(url => 
  /drive\.google\.com|docs\.google\.com|notion\.so|dropbox\.com/i.test(url)
);

const productUrls = [...new Set(allUrls)].filter(url => 
  !excludePatterns.some(pattern => pattern.test(url)) &&
  !briefUrls.includes(url)
);

// Extract latest message only (remove reply chains)
let latestMessage = emailBody;
const replyMarkers = [
  /On .{10,50} wrote:/i,
  /From:.*?Sent:.*?To:/is,
  /----+ ?Original Message ?----+/i,
  /_{10,}/
];
for (const pattern of replyMarkers) {
  const match = emailBody.match(pattern);
  if (match && match.index > 50) {
    latestMessage = emailBody.substring(0, match.index).trim();
    break;
  }
}

return {
  json: {
    emailId: email.id || '',
    threadId: email.threadId || '',
    from: fromRaw,
    senderEmail: senderEmail,
    senderDomain: senderDomain,
    companyUrl: companyUrl,
    subject: subject,
    latestMessage: latestMessage.substring(0, 2500),
    fullEmailBody: emailBody.substring(0, 4000),
    productUrls: productUrls.slice(0, 5),
    briefUrls: briefUrls.slice(0, 3),
    labelId: config.labelId,
    firecrawlApiKey: config.firecrawlApiKey,
    processedLabelId: config.processedLabelId
  }
};

