const aiResult = $input.item.json || {};
const emailData = $('Format Email').item.json;
const extractedData = $('Merge Extracted Data1').item.json;

let output = aiResult.output || aiResult.text || '';

// Fallback if AI failed
if (!output || output.length < 100) {
  const senderName = extractedData.senderFirstName || 'there';
  const productName = extractedData.productName || 'your product';
  const companyName = extractedData.companyName || 'your company';
  output = '<p>Hey ' + senderName + ',</p>' +
    '<p>Thanks for reaching out about ' + productName + '! I\'d love to explore a video collaboration.</p>' +
    '<p>To create the best angle for my audience (30K+ senior devs and technical founders), could you share:</p>' +
    '<ul>' +
    '<li>Product access or demo account</li>' +
    '<li>Key features you want highlighted</li>' +
    '<li>Any product brief or messaging doc</li>' +
    '</ul>' +
    '<p>Once I have access, I\'ll research the space and come back with a unique video direction that stands out from existing content.</p>' +
    '<p>Best,<br>Eric</p>';
}

// Clean markdown code blocks if present
if (output.startsWith('```html')) {
  output = output.replace(/^```html\n?/, '').replace(/\n?```$/, '');
} else if (output.startsWith('```')) {
  output = output.replace(/^```\n?/, '').replace(/\n?```$/, '');
}

// Clean any remaining markdown
output = output.replace(/```/g, '');

return {
  json: {
    emailId: emailData.emailId,
    threadId: emailData.threadId,
    labelId: emailData.labelId,
    processedLabelId: emailData.processedLabelId,
    replyContent: output.trim()
  }
};
