function generateRandomPassword(minLength, maxLength) {
    // Define characters to use in the password
    const lowercase = 'abcdefghijklmnopqrstuvwxyz';
    const uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const numbers = '0123456789';
    const specialCharacters = '!@#$%^&*_+';

    // Combine all character sets
    const allCharacters = lowercase + uppercase + numbers + specialCharacters;

    // Calculate the password length within the given range
    const passwordLength = Math.floor(Math.random() * (maxLength - minLength + 1)) + minLength;

    // Initialize the password
    let password = '';

    // Ensure the password includes at least one character from each character set
    password += lowercase.charAt(Math.floor(Math.random() * lowercase.length));
    password += uppercase.charAt(Math.floor(Math.random() * uppercase.length));
    password += numbers.charAt(Math.floor(Math.random() * numbers.length));
    password += specialCharacters.charAt(Math.floor(Math.random() * specialCharacters.length));

    // Fill the rest of the password with random characters from all character sets
    for (let i = password.length; i < passwordLength; i++) {
        password += allCharacters.charAt(Math.floor(Math.random() * allCharacters.length));
    }

    // Shuffle the password to ensure randomness
    password = password.split('').sort(() => Math.random() - 0.5).join('');

    return password;
}

