// Get data from previous nodes by name
const sshData = $input.first().json.stdout;
const vmData = $('API - VM List').first().json.data;
const nodeData = $('API - Node Status').first().json.data;
const tasksData = $('API - Node Tasks').first().json.data;

if (!vmData) {
  throw new Error('VM data not received from API - VM List node');
}
if (!nodeData) {
  throw new Error('Node status data not received from API - Node Status node');
}
if (!tasksData) {
  throw new Error('Tasks data not received from API - Node Tasks node');
}

// Parse SSH output for temperature
const sshLines = (sshData || '').split('\n');
const tempLines = sshLines.filter(l => l.includes('Package') || l.includes('Core'));

let temperatureDisplay = 'No temperature data available';
let hasTempWarning = false;

if (tempLines.length > 0) {
  const tempData = [];
  let hasHighTemp = false;

  for (const line of tempLines) {
    const currentMatch = line.match(/([+\-]?[0-9.]+)Â°C/);
    const highMatch = line.match(/high\s*=\s*([+\-]?[0-9.]+)Â°C/);

    if (currentMatch && highMatch) {
      const current = parseFloat(currentMatch[1]);
      const high = parseFloat(highMatch[1]);
      const label = line.split(':')[0].trim();

      tempData.push({ label, current, high, line: line.trim() });

      if (current >= high) {
        hasHighTemp = true;
        hasTempWarning = true;
      }
    }
  }

  if (hasHighTemp) {
    temperatureDisplay = tempData.map(t => t.line).join('\n');
  } else {
    const avgTemp = (tempData.reduce((sum, t) => sum + t.current, 0) / tempData.length).toFixed(1);
    const maxTemp = Math.max(...tempData.map(t => t.current)).toFixed(1);
    const minTemp = Math.min(...tempData.map(t => t.current)).toFixed(1);
    temperatureDisplay = `Average: ${avgTemp}Â°C (Min: ${minTemp}Â°C, Max: ${maxTemp}Â°C)`;
  }
}

// Parse uptime from SSH
let uptimeString = 'N/A';
const uptimeLine = sshLines.find(l => l.includes('up'));
if (uptimeLine) {
  uptimeString = uptimeLine.trim();
}

// VM statistics
const totalVMs = vmData.length;
const runningVMs = vmData.filter(vm => vm.status === 'running').length;
const stoppedVMs = totalVMs - runningVMs;

// Find recently stopped VMs from task log
const now = Math.floor(Date.now() / 1000);
const fifteenMinutesAgo = now - 900;

const recentStopTasks = tasksData.filter(task => {
  const isStopTask = task.type === 'qmstop' || task.type === 'qmshutdown';
  const taskTime = task.starttime || task.endtime || 0;
  return isStopTask && taskTime >= fifteenMinutesAgo;
});

const recentlyStoppedVMs = recentStopTasks.map(task => {
  let vmid = 'N/A';
  vmid = task.id;

  const vm = vmData.find(v => String(v.vmid) === String(vmid));
  const vmName = vm ? vm.name : 'Unknown';

  const taskTime = task.starttime || task.endtime || now;
  const minutesAgo = Math.floor((now - taskTime) / 60);

  return {
    id: vmid,
    name: vmName,
    minutesAgo: minutesAgo,
    status: task.status || 'stopped'
  };
});

const recentlyStopped = recentlyStoppedVMs.length;

// Node statistics from API
const cpuUsage = nodeData.cpu ? (nodeData.cpu * 100).toFixed(2) + '%' : 'N/A';
const memTotal = nodeData.memory ? (nodeData.memory.total / (1024**3)).toFixed(2) + ' GB' : 'N/A';
const memUsed = nodeData.memory ? (nodeData.memory.used / (1024**3)).toFixed(2) + ' GB' : 'N/A';
const memPercent = nodeData.memory ? ((nodeData.memory.used / nodeData.memory.total) * 100).toFixed(2) + '%' : 'N/A';
const uptimeSeconds = nodeData.uptime || 0;
const uptimeDays = Math.floor(uptimeSeconds / 86400);
const uptimeHours = Math.floor((uptimeSeconds % 86400) / 3600);
const uptimeFormatted = `${uptimeDays}d ${uptimeHours}h`;

return {
  timestamp: new Date().toISOString(),
  vms: {
    total: totalVMs,
    running: runningVMs,
    stopped: stoppedVMs,
    recentlyStopped: recentlyStopped,
    recentlyStoppedDetails: recentlyStoppedVMs
  },
  host: {
    cpu: cpuUsage,
    memoryUsed: memUsed,
    memoryTotal: memTotal,
    memoryPercent: memPercent,
    uptime: uptimeFormatted,
    uptimeDetailed: uptimeString,
    temperature: temperatureDisplay,
    tempWarning: hasTempWarning
  }
};

const data = $input.first().json;

const tempWarningEmoji = data.host.tempWarning ? 'ğŸ”¥ ' : '';
const tempTitle = data.host.tempWarning ? 'Temperature Warning' : 'Temperature Data';

let recentlyStoppedSection = '';
if (data.vms.recentlyStopped > 0 && data.vms.recentlyStoppedDetails && data.vms.recentlyStoppedDetails.length > 0) {
  const vmList = data.vms.recentlyStoppedDetails
    .map(vm => `  â€¢ VM ${vm.id} - ${vm.name} (${vm.minutesAgo} min ago)`)
    .join('\n');
  recentlyStoppedSection = `\n\n<b>Recently Stopped VMs Details:</b>\n${vmList}`;
}

const message = `<b>Proxmox Monitoring Report</b>
<i>Generated: ${new Date(data.timestamp).toLocaleString('en-US')}</i>

<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>
<b>Virtual Machines</b>
<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>

<b>Total VMs:</b> ${data.vms.total}
<b>Running:</b> ${data.vms.running} âœ…
<b>Stopped:</b> ${data.vms.stopped} â›”
<b>Recently Stopped:</b> ${data.vms.recentlyStopped} âš ï¸${recentlyStoppedSection}

<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>
<b>Host Resources</b>
<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>

<b>CPU Usage:</b> ${data.host.cpu}
<b>Memory:</b> ${data.host.memoryUsed} / ${data.host.memoryTotal}
           (${data.host.memoryPercent})
<b>Uptime:</b> ${data.host.uptime}

<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>
<b>${tempWarningEmoji}${tempTitle}</b>
<b>â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”</b>

<code>${data.host.temperature}</code>`;

return { message };
