// Extract job data based on actual HTML structure from jobfind.gr
const html = $input.all()[0].json.data;
const jobs = [];

// Split by jobitem divs which contain the full job listing
const jobItemPattern = /<div class="jobitem[^>]*>([\s\S]*?)<\/div>\s*<\/div>\s*<\/div>\s*<\/div>/g;
let jobMatch;

while ((jobMatch = jobItemPattern.exec(html)) !== null) {
  const jobContent = jobMatch[1];

  // Extract date from col-xs-6 div (usually contains datemob span)
  const dateMatch = jobContent.match(/<span class="datemob">([^<]+)<\/span>/);
  const date = dateMatch ? dateMatch[1].trim() : 'Not specified';

  // Extract title and URL from h3 > a
  const titleMatch = jobContent.match(/<h3[^>]*>\s*<a[^>]*href="([^"]+)"[^>]*>([^<]+)<\/a>/);
  if (!titleMatch) continue;

  const urlPath = titleMatch[1];
  const title = titleMatch[2].trim();
  const url = urlPath.startsWith('http') ? urlPath : 'https://www.jobfind.gr' + urlPath;

  // Extract location from city div
  const locationMatch = jobContent.match(/<div class="city"[^>]*>\s*<img[^>]*>\s*([^<]+)<\/div>/);
  const location = locationMatch ? locationMatch[1].trim() : 'Not specified';

  // Extract working model from type div
  const typeMatch = jobContent.match(/<div class="type"[^>]*>\s*<img[^>]*>\s*([^<]+)<\/div>/);
  const workingModel = typeMatch ? typeMatch[1].trim() : 'Not specified';

  jobs.push({
    date: date,
    title: title,
    location: location,
    workingModel: workingModel,
    url: url
  });
}

return jobs;

