// Step 1: Get the HTML content.
const htmlContent = $input.item.json.data;

// Step 2: Use regex to extract the entire 'window.jobBoard = { ... }' assignment.
// We look for the variable assignment and capture everything until the closing script tag or end of string.
const assignmentRegex = /window\.jobBoard\s*=\s*(\{[\s\S]*?\})\s*;/i;
const assignmentMatch = htmlContent.match(assignmentRegex);

if (!assignmentMatch || !assignmentMatch[1]) {
    return [{ json: { error: "Failed to locate the 'window.jobBoard' variable assignment." } }];
}

// The captured content is the full JavaScript object (not yet strict JSON).
let jobBoardContent = assignmentMatch[1].trim();

// Step 3: Use eval() to safely parse the JavaScript object string into an object.
// The surrounding 'window.jobBoard =' is stripped, leaving only the object literal { ... }.
try {
    // ⚠️ Security Note: eval() is used as a last resort to parse the complex JS object literal.
    // We execute the string, which assigns its content to a new variable.
    const variableName = 'tempJobBoardData';
    eval(`var ${variableName} = ${jobBoardContent}`);

    // Retrieve the variable created by eval()
    const jobBoardObject = global[variableName];

    // Step 4: Navigate to the desired job data structure.
    const jobData = jobBoardObject.initialState["api/v1/jobs"].data;

    if (!jobData || !Array.isArray(jobData.jobs)) {
        return [{ json: { error: "Job data structure is invalid: 'jobs' array not found." } }];
    }

    // Step 5: Create one n8n output item for each job.
    const outputItems = jobData.jobs.map(job => ({
        json: job
    }));

    // Clean up the global variable
    delete global[variableName];

    return outputItems;

} catch (error) {
    console.error("Failed to execute eval() or access nested properties:", error);
    return [{ json: { 
        error: "Failed to parse JavaScript object using eval().", 
        details: error.message
    } }];
}

const md = items[0].json.output;  // AI text output

// Convert Markdown table into an array of rows
function mdTableToHtml(mdTable) {
  const lines = mdTable.trim().split("\n");

  // Filter lines that are part of the table
  const rows = lines.filter(line => line.startsWith("|"));

  const htmlRows = rows.map((line, index) => {
    // Split columns
    const cols = line.split("|").slice(1, -1).map(c => c.trim());

    if (index === 0) {
      // Header row
      return "<tr>" + cols.map(c => `<th style="padding: 8px; background:#f4f4f4; border:1px solid #ddd;">${c}</th>`).join("") + "</tr>";
    }

    // Skip alignment row (---)
    if (line.includes("---")) return "";

    // Data rows
    return "<tr>" + cols.map(c => `<td style="padding: 8px; border:1px solid #ddd;">${c}</td>`).join("") + "</tr>";
  }).join("\n");

  return `
    <table style="border-collapse: collapse; width: 100%; margin-top: 20px;">
      ${htmlRows}
    </table>
  `;
}

// Convert markdown table to HTML
const htmlTable = mdTableToHtml(md);

return [{
  json: {
    html: `
    <html>
      <body style="font-family: Arial, sans-serif; color: #333; padding: 20px;">

        <h1 style="color:#0077cc;">IT Job Market Summary Report</h1>

        <p>Below is a structured summary of the latest IT job openings collected from NewJobs.gr.</p>

        ${htmlTable}

        <br><br>
        <p style="font-size: 12px; color:#777;">
          Generated automatically by n8n AI · ${new Date().toLocaleString("en-GB", { timeZone: "Europe/Athens" })}
        </p>

      </body>
    </html>
    `
  }
}];

